/*  * File:   IRCStatusListener.cpp * Author: Florian *  * Created on 20. Februar 2015, 23:10 */#include "IRCStatusListener.h"#include "IRCBot.h"#include "IRCConnection.h"IRCStatusListener::IRCStatusListener(IRCBot *bot) {    this->bot = bot;}IRCStatusListener::IRCStatusListener(const IRCStatusListener& orig) {}IRCStatusListener::~IRCStatusListener() {}void IRCStatusListener::Analyze(std::string message){    std::string ping = "PING";    if(message.substr(0, ping.size()) == ping){        message = message.replace(0, std::string("PING").length(), "PONG");        message = message + "\n";        this->bot->connection->sendToIRC(message);    }}